mult=$(echo "print(int(${SLURM_CPUS_ON_NODE}/${#gpu_array[@]}) + 1)" | python -u)
gpu_array=( $(echo "print('${gpu_array[@]} ' * ${mult})" | python -u) )
[ ! -f rerun/rerun_struct${1}_coord${2}.edr ] && gmx mdrun -gpu_id ${gpu_array[${3}]} -ntomp ${n_cpus} -rerun lambda${2}/lambda.${ext} -s lambda${1}/lambda_nocont.tpr -e rerun/rerun_struct${1}_coord${2}.edr -g rerun/rerun_struct${1}_coord${2}.log >& rerun/rerun_struct${1}_coord${2}.out
}

fn0_cpu () {
[ ! -f rerun/rerun_struct${1}_coord${2}.edr ] && gmx mdrun -ntomp ${n_cpus} -rerun lambda${2}/lambda.${ext} -s lambda${1}/lambda_nocont.tpr -e rerun/rerun_struct${1}_coord${2}.edr -g rerun/rerun_struct${1}_coord${2}.log >& rerun/rerun_struct${1}_coord${2}.out
}

fn0_vanilla () {
[ ! -f rerun/rerun_struct${1}_coord${2}.edr ] && gmx mdrun -rerun lambda${2}/lambda.${ext} -s lambda${1}/lambda_nocont.tpr -e rerun/rerun_struct${1}_coord${2}.edr -g rerun/rerun_struct${1}_coord${2}.log >& rerun/rerun_struct${1}_coord${2}.out
}

input_n_jobs=-1

if [[ "x${input_n_jobs}" == "x-1" ]]
then
if [[ "x$SLURM_CPUS_ON_NODE" != "x" ]]
then
# This is a slurm scheduler
if [[ "x$CUDA_VISIBLE_DEVICES" != "x" ]]
then
# There are GPU assigned to this job. Use a job per GPU.
gpu_array=(${CUDA_VISIBLE_DEVICES//,/ })
n_jobs=${#gpu_array[@]}
n_cpus=$(echo "print(int($SLURM_CPUS_ON_NODE / $n_jobs))" | python3 -u)
fn() { fn0_cpu_gpu "$@" ; }
else
# There are no GPU. Use 2 CPU per run
n_cpus=2
n_jobs=$(echo "print(int($SLURM_CPUS_ON_NODE / $n_cpus))" | python3 -u)
fn() { fn0_cpu "$@" ; }
fi
elif [[ "x$PBS_NODEFILE" != "x" ]]
then
# This is a PBS scheduler
tot_cpus=$(cat $PBS_NODEFILE | wc -l)
if [[ "x$PBS_GPUFILE" != "x" ]]
then
# There are GPU assigned to this job. Use a job per GPU.
CUDA_VISIBLE_DEVICES=$(cat $PBS_GPUFILE | awk -F"-gpu" '{ printf A$2;A=","}')
gpu_array=(${CUDA_VISIBLE_DEVICES//,/ })
n_jobs=${#gpu_array[@]}
n_cpus=$(echo "print(int($tot_cpus / $n_jobs))" | python3 -u)
fn() { fn0_cpu_gpu "$@" ; }
else
# There are no GPU. Use 2 CPU per run
n_cpus=2
n_jobs=$(echo "print(int($tot_cpus / $n_cpus))" | python3 -u)
fn() { fn0_cpu "$@" ; }
fi
else
# This is another scheduler or Bash, fall back to 1 job
n_jobs=1
fn() { fn0_vanilla "$@" ; }
fi
else
n_jobs=${input_n_jobs}
fn() { fn0_vanilla "$@" ; }
fi
export -f fn
export -f fn0_cpu_gpu
export -f fn0_cpu
export -f fn0_vanilla
export n_cpus
export ext
export CUDA_VISIBLE_DEVICES

parallel --version > /dev/null 2>&1 || { echo "[ERROR] GNU parallel not found in $PATH, cannot continue" && exit -1; }

n_lambda=$(ls lambda*/lambda.tpr | wc -l)
lambda_exp=$(seq 0 $(echo ${n_lambda}-1 | bc))
echo $n_jobs
parallel --env fn0_cpu_gpu --env fn0_cpu --env fn0_vanilla --env fn --env n_cpus --env CUDA_VISIBLE_DEVICES --env ext --joblog rerun_joblog_$(date "+%H%M%S_%d%m%Y").log --jobs $n_jobs "fn {1} {2} {%}" ::: ${lambda_exp} ::: ${lambda_exp}
[ $(ls rerun/rerun_struct*_coord*.edr | wc -l) -ne $(echo ${n_lambda}^2 | bc) ] && { echo "Failed to during rerun step on line $LINENO"; exit 1; }


echo "$(date): Starting collect in FXR_12-FXR_74 water at $(hostname)"

cd rerun || { echo "Could not cd into rerun directory, cannot continue" && exit -1; }

input_n_jobs=-1

if [[ "x${input_n_jobs}" == "-1" ]]
then
collect_n_jobs=1
else
collect_n_jobs=${input_n_jobs}
fi

parallel --jobs $collect_n_jobs '[ ! -f lambda{}.xvg ] && printf "pV\n\n" | gmx energy -f ../lambda{}/lambda.edr -o lambda{}.xvg' ::: ${lambda_exp}
parallel --jobs $collect_n_jobs '[ ! -f {.}.xvg ] && printf "Potential\n\n" | gmx energy -f {} -o {.}.xvg' ::: *.edr

[ $(ls rerun_struct*_coord*.xvg lambda*.xvg | wc -l) -ne $(echo ${n_lambda}^2 + ${n_lambda} | bc) ] && "Failed during collect step on line $LINENO"

chmod +x ../../../../collect_results_from_xvg && ./../../../../collect_results_from_xvg --temperature 298.15 --gmx_bin gmx --input *.xvg
